import React, { useEffect, useState, useRef } from 'react';
import './HomePage.css';
import useToken from '../../service/UseToken';
import { useNavigate } from 'react-router-dom';
import { getToken, getCurrentUser, getDefaultProfilePicture, createPost, getFriendsPosts, getImagesByPostId } from '../../service/BackendService';
import { Link } from 'react-router-dom';
import ProtectedRoutes from '../../ProtectedRoutes';

const HomePage = () => {
  const { clearToken } = useToken();
  const [userData, setUserData] = useState(null);
  const [profilePictureUrl, setProfilePictureUrl] = useState(null);
  const [friendsPosts, setFriendsPosts] = useState([]);
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);
  const navigate = useNavigate();
  const inputRefTitle = useRef(null);
  const inputRefDescription = useRef(null);
  const [Title, setTitle] = useState('');
  const [Description, setDescription] = useState('');

    const isFormValid = () => {
   if (inputRefDescription.current !== null && inputRefTitle.current !== null) {
   return (
      inputRefDescription.current.value.trim() !== '' &&
      inputRefTitle.current.value.trim() !== '' 
   ); }
 };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const user = await getCurrentUser();
        setUserData(user);

        const pictureUrl = await getDefaultProfilePicture();
        setProfilePictureUrl(pictureUrl);

        let posts = await getFriendsPosts();
        if (posts && posts.length > 0) {
          const postsWithImages = await Promise.all(posts.map(async post => {
            const images = await getImagesByPostId(post.id);
            return { ...post, images };
          }));
          setFriendsPosts(postsWithImages);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    if (getToken()) {
      fetchData();
    }
  }, [getToken()]); // Re-run when token changes

  const handleLogout = () => {
    clearToken();
    navigate('/');
  };

  const handleCreatePost = async (e) => {
    e.preventDefault();

    try {
      const fileInput = document.getElementById('ImagePath');
      const files = fileInput.files;

      if (files.length > 0) {
        const file = files[0];
        const reader = new FileReader();

        reader.onloadend = async () => {
          const ImagePath = reader.result;
          try {
            const response = await createPost({ Title, Description, ImagePath });
            setFriendsPosts(prevPosts => [{ ...response, images: [ImagePath] }, ...prevPosts]);
            setTitle('');
            setDescription('');
            setShowSuccessMessage(true);
            setTimeout(() => {
              setShowSuccessMessage(false);
            }, 5000);
          } catch (error) {
            console.error('Error creating post:', error);
          }
        };

        reader.readAsDataURL(file);
      } else {
        const response = await createPost({ Title, Description });
        setFriendsPosts(prevPosts => [response, ...prevPosts]);
        setTitle('');
        setDescription('');
        setShowSuccessMessage(true);
        setTimeout(() => {
          setShowSuccessMessage(false);
        }, 5000);
      }
    } catch (error) {
      console.error('Error handling file upload:', error);
    }
  };

  const formatDateTime = (dateTimeString) => {
    const dateTime = new Date(dateTimeString);
    return dateTime.toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      second: 'numeric',
      hour12: true,
    });
  };

  return (
    <div>
      <ProtectedRoutes />
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand">Navbar</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item active">
              <Link to="/home" style={{ textDecoration: 'none' }}>
                <a className="nav-link">Home</a>
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/profile/friends" style={{ textDecoration: 'none' }}>
                <a className="nav-link">Friends</a>
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/profile" style={{ textDecoration: 'none' }}>
                <a className="nav-link">Profile</a>
              </Link>
            </li>
            <li className="nav-item">
              <button onClick={handleLogout} className="nav-link">LOG OUT</button>
            </li>
          </ul>
        </div>
      </nav>

      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <div id="content" className="content content-full-width">
              <div className="profile">
                <div className="profile-header">
                  <div className="profile-header-cover"></div>
                  <div className="profile-header-content">
                    <div className="profile-header-img">
                      <img src={profilePictureUrl} alt=""/>
                    </div>
                    <div className="profile-header-info">
                      <h4 className="m-t-10 m-b-5">{userData?.FirstName} {userData?.LastName}</h4>
                    </div>
                  </div>
                </div>
              </div>

              <div className="profile-content">
                <div className="tab-content p-0">
                  <div className="tab-pane fade active show" id="profile-post">
                    <ul className="timeline">
                      <div className="timeline-body">
                        <h5>Post something</h5>
                        <form onSubmit={handleCreatePost}>
                          <div className="form-group">
                            <label htmlFor="Title">Title <span className="require">*</span></label>
                            <input ref={inputRefTitle} type="text" className="form-control" name="Title" value={Title} onChange={(e) => setTitle(e.target.value)} />
                          </div>
                          <div className="form-group">
                            <label htmlFor="Description">Description <span className="require">*</span></label>
                            <textarea rows="5" ref={inputRefDescription} className="form-control" name="description" value={Description} onChange={(e) => setDescription(e.target.value)}></textarea>
                          </div>
                          <div className="form-group">
                            <label htmlFor="ImagePath">Upload Image(s)</label>
                            <input type="file" className="ImagePath" id="ImagePath" multiple accept="image/*" />
                          </div>
                          <button type="submit" className="btn btn-primary" disabled={!isFormValid()}>
                            Post
                          </button>
                          {showSuccessMessage && <div className="alert alert-success mt-3" role="alert">Posted successfully!</div>}
                        </form>
                      </div>
                      {friendsPosts && friendsPosts.sort((a, b) => new Date(b.DateTimeCreated) - new Date(a.DateTimeCreated)).map((response) => (
                        <li key={response.DateTimeCreated}>
                          <div className="timeline-body">
                            <div className="timeline-header">
                              <span className="userimage"><img src="https://bootdey.com/img/Content/avatar/avatar3.png" alt="" /></span>
                              <span className="username"><a href="">{response.Title}</a></span>
                              <br/>
                              <span className="username">{response.FirstName} {response.LastName}</span>
                            </div>
                            <div className="timeline-content">
                              <p>{response.Description}</p>
                              {response.images && response.images.map((image, index) => (
                                <div key={index} className="postImages">
                                  <img src={image.path} alt="Post image" style={{ maxWidth: '100%' }} />
                                </div>
                              ))}
                            </div>
                            <div className="timeline-footer">
                              <a href="" className="m-r-15 text-inverse-lighter"><i className="fa fa-thumbs-up fa-fw fa-lg m-r-3"></i> Like</a>
                              <a href="" className="m-r-15 text-inverse-lighter"><i className="fa fa-comments fa-fw fa-lg m-r-3"></i> Comment</a>
                            </div>
                          </div>
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
