function setToken(token) {
    localStorage.setItem('token', token);
  }
  
  function getToken() {
    return localStorage.getItem('token');
  }
  
  async function loginUser(credentials) {
    return fetch('http://localhost:9000/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
      mode: 'cors',
    })
      .then((data) => data.json())
      .then((response) => {
        setToken(response.token);
        console.log(response);
        return response;
      })
      .catch((error) => console.error('Fetch error:', error));
  }

  async function registerUser(userData) {
  return fetch('http://localhost:9000/api/registration', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(userData),
    mode: 'cors',
  })
    .then((data) => data.json())
    .then((response) => {
      console.log(response);
      return response;
    })
    .catch((error) => console.error('Fetch error:', error));
}


async function getCurrentUser() {
  const token = getToken();

  if (!token) {
    console.error('Token not found');
    return null; 
  }

  try {
    const response = await fetch('http://localhost:9000/api/get/user', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`, 
      },
    });

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }

    const userData = await response.json();
    console.log(userData); 

    return userData;
  } catch (error) {
    console.error('Fetch error:', error);
    throw error; 
  }
}
    
  export { setToken, getToken, loginUser, registerUser, getCurrentUser  };
